mapper.update:
update	USER_TB 
set		UNREGIST = 'USR02'
where 	ID = #{ID}

update	USER_TB 
set		UNREGIST = 'USR00'
where	ID = #{ID}

mapper.select:
select	UNREGIST
from	USER_TB
where	ID = #{ID}

select	STATUS
from	PROJECT_TB
where	IDX = #{PJ_IDX}

mapper.update:
update	PROJECT_TB  
set		STATUS = 'PJT01'
where	IDX = #{PJ_IDX}

update	PROJECT_TB
set		STATUS = 'PJT00'
where	IDX = #{PJ_IDX}

mapper.select:
