mapper.insert:
insert into USER_TB( IDX
			       , NAME
			       , ID
			       , PASS 
			       , PROFILE
			       , PHONE
			       , PREFER
			       , EVENT_ALR)

		 	 values( (SELECT IDX
	    				FROM (SELECT IFNULL(MAX(IDX), 0) + 1 AS IDX
			    		FROM USER_TB) A)
		 	       , #{name}
			       , #{id}
			       , #{pass} 
			       , #{profile}
			       , #{phone}
			       , #{prefer}
			       , #{eventAlr})

insert into ADDRESS_TB( IDX
					  , ID
					  , ADDRESS_NM
					  , NAME
					  , PHONE
					  , ADDRESS
					  , ZIP_CODE)

			 	values( #{idx}
			 		  , #{id}
			 		  , #{addressNm}
			 		  , #{name}
			 		  , #{phone}
			 		  , #{address}
			 		  , #{zipCode})

mapper.select:
select * 
  from USER_TB

 where ID=#{id}
   and PASS=#{pass}
